<?phpnamespace console\controllers;use common\models\Setting;use console\components\BookImporter;class ImportBooksController extends \yii\console\Controller{    public function actionIndex()    {        echo "Type `yii import-books/import` to run import process";    }    public function actionImport()    {        $urlSetting = Setting::getSettingByKey(Setting::SOURCE_URL_KEY);        if (empty($urlSetting))        {            echo "Missing source url" . PHP_EOL;            return;        }        $bookImporter = new BookImporter(['url' => $urlSetting]);        $bookImporter->parse();    }}